// Filename: WhoisForm.jsx

import React, { useState } from 'react';
import DomainInfoTable from './DomainInfoTable';
import ContactInfoTable from './ContactInfoTable';

function WhoisForm() {
  const [domain, setDomain] = useState('');
  const [whoisData, setWhoisData] = useState(null);
  const [error, setError] = useState('');
  const [selectedTable, setSelectedTable] = useState('tblDomainInfo');

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setWhoisData(null);

    try {
      const response = await fetch(process.env.REACT_APP_API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ domain }),
      });

      const data = await response.json();

      if (response.ok && data.WhoisRecord) {
        setWhoisData(data.WhoisRecord);
        console.log('WHOIS Data:', data.WhoisRecord);
      } else {
        setError(data.error || 'Invalid domain or WHOIS data not found.');
      }
    } catch (err) {
      setError('Request failed: ' + err.message);
    }
  };

  return (
    <div className="flex justify-left items-start py-10 rounded-lg w-full max-w-6xl p-6 mx-4">
        <h1 className="text-2xl font-semibold mb-4">WHOIS Lookup</h1>

        <label htmlFor="domain" className="flex text-white mb-1">Domain Name:</label>
        <form onSubmit={handleSubmit} className="mb-4 flex flex-col sm:flex-row gap-2">
          <input
            id="domain"
            type="text"
            value={domain}
            onChange={(e) => setDomain(e.target.value)}
            placeholder="Enter domain (e.g. example.com)"
            required
            className="border rounded px-4 py-2 w-full sm:flex-1 text-black"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition w-full sm:w-auto"
          >
            Submit
          </button>
        </form>

        <label htmlFor="tableSelect" className="flex text-white">Select Information: </label>
        <select
          id="tableSelect"
          value={selectedTable}
          onChange={handleTableChange}
          className="border rounded px-4 py-2 w-full text-black mb-4"
        >
          <option value="tblDomainInfo">Domain Information</option>
          <option value="tblContactInfo">Contact Information</option>
        </select>

        {error && <p className="text-red-600 mb-4">{error}</p>}

        {selectedTable === 'tblDomainInfo' && <DomainInfoTable data={whoisData} />}
        {selectedTable === 'tblContactInfo' && <ContactInfoTable data={whoisData} />}
      </div>
  );
}

export default WhoisForm;
