// Filename: client/src/components/WhoisForm.jsx
import React, { useState } from 'react';

function WhoisForm() {
  const [domain, setDomain] = useState('');
  const [whoisData, setWhoisData] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setWhoisData(null);

    try {
      const response = await fetch('http://localhost:5000/api/whois', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ domain })
      });

      const data = await response.json();

      if (response.ok) {
        setWhoisData(data);
        console.log("WHOIS Data:", data);
      } else {
        setError(data.error || 'Unknown error');
      }
    } catch (err) {
      setError('Request failed: ' + err.message);
    }
  };

  return (
    <div>
      <h2>WHOIS Lookup</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={domain}
          onChange={(e) => setDomain(e.target.value)}
          placeholder="Enter domain (e.g. example.com)"
          required
        />
        <button type="submit">Lookup</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {whoisData && (
        <pre style={{ textAlign: 'left', background: '#f4f4f4', padding: '1rem' }}>
          {JSON.stringify(whoisData, null, 2)}
        </pre>
      )}
    </div>
  );
}

export default WhoisForm;
