// Filename: client/src/components/WhoisForm.jsx
import React, { useState } from 'react';

function WhoisForm() {
  const [domain, setDomain] = useState('');
  const [whoisData, setWhoisData] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setWhoisData(null);

    try {
      const response = await fetch('http://localhost:5000/api/whois', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ domain })
      });

      const data = await response.json();

      if (response.ok) {
        setWhoisData(data);
        alert("Domain Name: " + data.WhoisRecord.domainName);
        console.log("WHOIS Data:", data);
      } else {
        setError(data.error || 'Unknown error');
      }
    } catch (err) {
      setError('Request failed: ' + err.message);
    }
  };

  const thStyle = {
    border: '1px solid #ccc',
    padding: '8px',
    backgroundColor: '#f9f9f9',
    textAlign: 'left',
  };

  const tdStyle = {
    border: '1px solid #ccc',
    padding: '8px',
  };

  return (
    <div>
      <h2>WHOIS Lookup</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={domain}
          onChange={(e) => setDomain(e.target.value)}
          placeholder="Enter domain (e.g. example.com)"
          required
        />
        <button type="submit">Lookup</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {whoisData && whoisData.WhoisRecord && (
        <table style={{ marginTop: '1rem', borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th style={thStyle}>Field</th>
              <th style={thStyle}>Value</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td style={tdStyle}>Domain Name</td>
              <td style={tdStyle}>{whoisData.WhoisRecord.domainName || 'N/A'}</td>
            </tr>
            <tr>
              <td style={tdStyle}>Created Date</td>
              <td style={tdStyle}>{whoisData.WhoisRecord.createdDate || 'N/A'}</td>
            </tr>
            <tr>
              <td style={tdStyle}>Expiry Date</td>
              <td style={tdStyle}>{whoisData.WhoisRecord.expiresDate || 'N/A'}</td>
            </tr>
            <tr>
              <td style={tdStyle}>Registrar Name</td>
              <td style={tdStyle}>{whoisData.WhoisRecord.registrarName || 'N/A'}</td>
            </tr>
            <tr>
              <td style={tdStyle}>Status</td>
              <td style={tdStyle}>
                {(whoisData.WhoisRecord.status || []).join(', ') || 'N/A'}
              </td>
            </tr>
          </tbody>
        </table>
      )}

    </div>
  );
}

export default WhoisForm;
