// Filename: WhoisForm.jsx

import React, { useState } from 'react';

function WhoisForm() {
  const [domain, setDomain] = useState('');
  const [whoisData, setWhoisData] = useState(null);
  const [error, setError] = useState('');
  const [selectedTable, setSelectedTable] = useState('tblDomainInfo');

  const handleTableChange = (e) => {
    setSelectedTable(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setWhoisData(null);

    try {
      const response = await fetch('http://localhost:5000/api/whois', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ domain }),
      });

      const data = await response.json();

      if (response.ok && data.WhoisRecord) {
        setWhoisData(data.WhoisRecord);
        console.log('WHOIS Data:', data.WhoisRecord);
      } else {
        setError(data.error || 'Invalid domain or WHOIS data not found.');
      }
    } catch (err) {
      setError('Request failed: ' + err.message);
    }
  };

  return (
    <div className="flex justify-left items-start py-10">
      <div className="rounded-lg w-full max-w-6xl p-6 mx-4">
        <h1 className="text-2xl font-semibold mb-4">WHOIS Lookup</h1>

          <label className="flex text-white">Domain Name: </label>
        <form onSubmit={handleSubmit} className="mb-4 flex flex-col sm:flex-row gap-2">
          <input
            type="text"
            value={domain}
            onChange={(e) => setDomain(e.target.value)}
            placeholder="Enter domain (e.g. example.com)"
            required
            className="border rounded px-4 py-2 w-full sm:flex-1 text-black"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700 transition w-full sm:w-auto"
          >
            Lookup
          </button>
        </form>

        <label className="flex text-white">Select Information: </label>
        <select
          value={selectedTable}
          onChange={handleTableChange}
          className="border rounded px-4 py-2 w-full text-black mb-4"
        >
          <option value="tblDomainInfo">Domain Information</option>
          <option value="tblContactInfo">Contact Information</option>
        </select>

        {error && <p className="text-red-600 mb-4">{error}</p>}

        {/* Table 1 */}
        {selectedTable === 'tblDomainInfo' && whoisData && (
          <div className="overflow-x-auto w-full">
            <table className="w-full table-fixed border-collapse text-sm">
              <thead className="bg-black-100">
                <tr>
                  <th className="border px-4 py-2 text-left">Domain Name</th>
                  <th className="border px-4 py-2 text-left">Registrar</th>
                  <th className="border px-4 py-2 text-left">Registration Date</th>
                  <th className="border px-4 py-2 text-left">Expiration Date</th>
                  <th className="border px-4 py-2 text-left">Estimated Domain Age</th>
                  <th className="border px-4 py-2 text-left">Hostnames</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td className="border px-4 py-2">{whoisData?.domainName || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.registrarName || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.createdDate || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.expiresDate || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.estimatedDomainAge || 'N/A'}</td>
                  <td className="border px-4 py-2">
                    {Array.isArray(whoisData.nameServers.hostNames)
                      ? whoisData.nameServers.hostNames.join(', ')
                      : whoisData.nameServers.hostNames || 'N/A'}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
        )}

        {/* Table 2 */}
        {selectedTable === 'tblContactInfo' && whoisData?.registrant && (
          <div className="overflow-x-auto w-full">
            <table className="w-full table-fixed border-collapse text-sm">
              <thead className="bg-black-100">
                <tr>
                  <th className="border px-4 py-2 text-left">Registrant Name</th>
                  <th className="border px-4 py-2 text-left">Technical Contact Name</th>
                  <th className="border px-4 py-2 text-left">Administrative Contact Name</th>
                  <th className="border px-4 py-2 text-left">Contact Email</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td className="border px-4 py-2">{whoisData?.registrant?.name || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.technicalContact?.name || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.administrativeContact?.name || 'N/A'}</td>
                  <td className="border px-4 py-2">{whoisData?.contactEmail || 'N/A'}</td>
                </tr>
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default WhoisForm;
