// Filename: client/src/components/WhoisForm.jsx
import React, { useState } from 'react';

function WhoisForm() {
  const [domain, setDomain] = useState('');
  const [whoisData, setWhoisData] = useState(null);
  const [error, setError] = useState('');

  const thStyle = {
    border: '1px solid #ccc',
    padding: '8px',
    backgroundColor: '#f9f9f9',
    textAlign: 'left',
  };

  const tdStyle = {
    border: '1px solid #ccc',
    padding: '8px',
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setWhoisData(null);

    try {
      const response = await fetch('http://localhost:5000/api/whois', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ domain }),
      });

      const data = await response.json();

      if (response.ok && data.WhoisRecord) {
        setWhoisData(data.WhoisRecord);
        console.log('WHOIS Data:', data.WhoisRecord);
      } else {
        setError(data.error || 'Invalid domain or WHOIS data not found.');
      }
    } catch (err) {
      setError('Request failed: ' + err.message);
    }
  };

  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', padding: '1rem' }}>
      <h2>WHOIS Lookup</h2>
      <form onSubmit={handleSubmit} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          value={domain}
          onChange={(e) => setDomain(e.target.value)}
          placeholder="Enter domain (e.g. example.com)"
          required
          style={{ padding: '0.5rem', width: '300px', marginRight: '0.5rem' }}
        />
        <button type="submit" style={{ padding: '0.5rem 1rem' }}>Lookup</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {whoisData && (
        <table border="1" style={{ marginTop: '1rem', width: '100%', borderCollapse: 'collapse' }}>
          <thead style={{ backgroundColor: '#f0f0f0' }}>
            <tr>
              <th style={{ padding: '8px' }}>Domain Name</th>
              <th style={{ padding: '8px' }}>Registrar</th>
              <th style={{ padding: '8px' }}>Created Date</th>
              <th style={{ padding: '8px' }}>Expiry Date</th>
              <th style={{ padding: '8px' }}>Status</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td style={{ padding: '8px' }}>{whoisData.domainName || 'N/A'}</td>
              <td style={{ padding: '8px' }}>{whoisData.registrarName || 'N/A'}</td>
              <td style={{ padding: '8px' }}>{whoisData.createdDate || 'N/A'}</td>
              <td style={{ padding: '8px' }}>{whoisData.expiresDate || 'N/A'}</td>
              <td style={{ padding: '8px' }}>
                {Array.isArray(whoisData.status)
                  ? whoisData.status.join(', ')
                  : whoisData.status || 'N/A'}
              </td>
            </tr>
          </tbody>
        </table>
      )}

    </div>
  );
}

export default WhoisForm;
